class BDphp3
{
    public function bd_php3($tmp_array, $k)
    {
        $count = 0;
        $length = count($tmp_array);
        if ($length < $k) {
            return false;
        }
        for ($i = 0; $i < $length; $i++) {
            $min = $i;
            for ($j = $i + 1; $j < $length; $j++) {
                if ($tmp_array[$j] < $tmp_array[$min]) {
                    $min = $j;
                }
            }
            $tmp = $tmp_array[$i];
            $tmp_array[$i] = $tmp_array[$min];
            $tmp_array[$min] = $tmp;
            $count++;
            if ($count > $k) {
                break;
            }
        }
        return array_slice($tmp_array, 0, $k);
    }
}
