    public function ExJosephRing_Logic($n, $m)
    {
        $tmp_array = array();
        // $left_over0
        $left_over = $n;
        // $ret_value
        $ret_value = 0;
        // 
        $start = 0;
        // 
        for ($i = 0; $i <= $n; $i++) {
            $tmp_array[$i] = $i;
        }
        $array_length = count($tmp_array);
        while ($left_over != 1) {
            for ($j = 1; $j <= $m; $j++) {
                $start++;
                // $tmp_array[$start] == 0
                if ($start != $array_length && $tmp_array[$start] == 0) {
                    for ($k = $start; $k < $array_length; $k++) {
                        if ($tmp_array[$k] == 0) {
                            $start++;
                        } elseif ($tmp_array[$k] > 0) {
                            break;
                        }
                    }
                }
                // 
                if ($start == $array_length) {
                    $start = 0;
                }
                if ($tmp_array[$start] == 0) {
                    for ($k = $start; $k < $array_length; $k++) {
                        if ($tmp_array[$k] == 0) {
                            $start++;
                        } elseif ($tmp_array[$k] > 0) {
                            break;
                        }
                    }
                }
            }

            $tmp_array[$start] = 0;

            // 0
            $left_over = 0;
            foreach ($tmp_array as $key => $value) {
                if ($value != 0) {
                    $left_over++;
                    $ret_value = $value;
                }
            }
        }
        return $ret_value;
    }
