public function XiErPaiXu($sort_array){
        if (!is_array($sort_array))
            return false;
        $n = count($sort_array);
        for ($gap = floor($n / 2); $gap > 0; $gap = floor($gap /= 2)) {
            for ($i = $gap; $i < $n; ++$i) {
                for ($j = $i - $gap; $j >= 0 && $sort_array[$j + $gap] < $sort_array[$j]; $j -= $gap) {
                    $temp = $sort_array[$j];
                    $sort_array[$j] = $sort_array[$j + $gap];
                    $sort_array[$j + $gap] = $temp;
                }
            }
        }
        return $sort_array;
    }
