public function Select($sort_array){
        for ($i = 0; $i < count($sort_array); $i++){
            $min = $i;
            for ($j = $i + 1; $j < count($sort_array); $j++){
                if ($sort_array[$min] > $sort_array[$j]){
                    $min = $j;
                }
            }
            $tmp = $sort_array[$min];
            $sort_array[$min] = $sort_array[$i];
            $sort_array[$i] = $tmp;
        }
        return $sort_array;
    }
