public function JiShu($sort_array, $pos) {
        $l = count($sort_array);
        $bucket = array();
        for($i = 0; $i < 10; $i++)
            $bucket[$i] = array(0);

        for($p = 1; $p <= $pos; $p++) {
            for($i = 0; $i < $l; $i++) {
                $n = $this->GetNumInPos($sort_array[$i], $p);
                $index = ++$bucket[$n][0];
                $bucket[$n][$index] = $sort_array[$i];
            }
            for($i=0, $j=0; $i<10; $i++) {
                for($num = 1; $num<=$bucket[$i][0]; $num++) {
                    $sort_array[$j++] = $bucket[$i][$num];
                }
                $bucket[$i][0] = 0;
            }
        }
        return $sort_array;
    }
